SELECT t1.flight_no, t1.airbus_no, t1.capacity, t1.city, air.city, t1.depart_date, t1.arrive_date, t1.depart_time, t1.arrive_time, t1.fare FROM airport air, (SELECT f.flight_no, f.airbus_no, f.capacity, a.city, f.destination_airport_code, f.depart_date, f.arrive_date, f.depart_time, f.arrive_time, f.fare FROM airport a, flight f where a.id_airport=f.from_airport_code) as t1  WHERE air.id_airport=t1.destination_airport_code;
